INSERT INTO `pyl_cartels`.`groupe` (`id`, `nom`, `budjet`, `description`) VALUES (DEFAULT, 'medellin', 782727, 'organisation criminelle colombienne spécialisée dans le trafic de cocaïne entre l\'Amérique du Sud et les États-Unis');
INSERT INTO `pyl_cartels`.`groupe` (`id`, `nom`, `budjet`, `description`) VALUES (DEFAULT, 'cali', 100000, 'organisation mafieuse colombienne de narcotrafiquants basée autour de la ville de Cali');
INSERT INTO `pyl_cartels`.`groupe` (`id`, `nom`, `budjet`, `description`) VALUES (DEFAULT, 'la_famillia', 65200752, 'La Familia Michoacana ou La Familia (en français la famille du Michoacán) est l\'un des sept grands cartels de la drogue mexicain');
INSERT INTO `pyl_cartels`.`groupe` (`id`, `nom`, `budjet`, `description`) VALUES (DEFAULT, 'juarez', 50000000, 'Le cartel de Juárez, ou organisation de Vicente Carrillo Fuentes (en), est un cartel de la drogue mexicain, basé à Ciudad Juárez où il s\'oppose violemment au cartel de Sinaloa, disposant pour ce faire d\'une organisation paramilitaire, La Línea');
INSERT INTO `pyl_cartels`.`groupe` (`id`, `nom`, `budjet`, `description`) VALUES (DEFAULT, 'los_zetas', 10057807, ' L\'organisation fut à l\'origine créée par le chef du cartel du Golfe, Osiel Cárdenas Guillen pour lui servir de bras armé jusqu\'à devenir aujourd\'hui indépendante');
INSERT INTO `pyl_cartels`.`groupe` (`id`, `nom`, `budjet`, `description`) VALUES (DEFAULT, 'dea', 5000000, 'Drug Enforcement Administration.  service de police fédéral du département de la Justice des États-Unis chargé de la mise en application de la loi sur les stupéfiants et de la lutte contre leur trafic.');
INSERT INTO `pyl_cartels`.`groupe` (`id`, `nom`, `budjet`, `description`) VALUES (DEFAULT, 'fbi', 90000000, 'aux États-Unis, le principal service fédéral de police judiciaire et un service de renseignement intérieur');
INSERT INTO `pyl_cartels`.`groupe` (`id`, `nom`, `budjet`, `description`) VALUES (DEFAULT, 'guardia_nacional', 60000, 'une institution armée de nature civile, chargée des mêmes missions que la police nationale des États-Unis mexicains, en garantissant la sécurité publique au nom de l’État fédéral');
INSERT INTO `pyl_cartels`.`groupe` (`id`, `nom`, `budjet`, `description`) VALUES (DEFAULT, 'senado_de_la_republica', 57252, 'chambre haute du Congrès du Mexique. Elle est composée de 128 sénateurs, intégralement renouvelés tous les six ans.');
INSERT INTO `pyl_cartels`.`groupe` (`id`, `nom`, `budjet`, `description`) VALUES (DEFAULT, 'sinaloa', 24272752, 'Le cartel de Sinaloa, aussi appelé l\'Organisation Reynosa, le cartel du Pacifique ou la Fédération, est une organisation criminelle mexicaine présente dans le monde entier, principalement dans les États de Baja California, Sinaloa, Durango, Sonora, Chihuahua et Tamaulipas, c\'est-à-dire dans le \" triangle d\'or \" et dans une des régions du Mexique dotées d\'une culture importante d\'opium et de marijuana');
